.header {
   &__wrapper {
      display: flex;
      justify-content: space-between;
      align-items: center;
      padding: 20px 0;

      @media screen and (max-width: 400px) {
         padding: 15px 0;
      }

      @media screen and (max-width: 700px) {
         align-items: flex-end;
      }
   }

   &__box {
      display: flex;
      justify-content: space-between;
      align-items: center;
      position: relative;

      @media screen and (max-width: 700px) {
         flex-direction: column;
         align-items: flex-start;
      }
   }

   &__logo {
      margin-right: 30px;

      span {
         display: block;
         font-weight: 800;
         font-size: 36px;
         line-height: 42px;
         color: $black;

         @media screen and (max-width: 700px) {
            margin-bottom: 20px;
         }

         @media screen and (max-width: 400px) {
            font-size: 30px;
         }
      }
   }
}

#searchResults {
   position: absolute;
   top: 58px;
   width: 515px;
   max-height: 228px;
   padding: 10px 20px;
   border-radius: 0 0 8px 8px;
   background-color: $white;
   opacity: .9;
   overflow: scroll;
   z-index: 100;

   @media screen and (max-width: 960px) {
      width: 340px;
   }

   @media screen and (max-width: 700px) {
      padding: 7px 20px;
   }

   @media screen and (max-width: 560px) {
      width: 240px;
   }

   @media screen and (max-width: 400px) {
      width: 195px;
      padding: 2px 20px;
   }
}

.search {
   &__wrapper {
      display: flex;
      align-items: center;
      padding: 5px;
      transition: all .3s ease-in;
      color: $black;

      &:hover {
         background-color: rgba(0, 0, 0, .1);;
      }
   }

   &__image {
      width: 40px;
      height: 50px;
      object-fit: cover;
      margin-right: 10px;
   }

   &__big-img {
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      max-width: 80%;
      max-height: 80%;
      z-index: 1000;
   }

   &__overlay {
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      background-color: rgba(0, 0, 0, 0.9);
      cursor: pointer;
      z-index: 999;
   }
}

