.swiper-container {
    height: 100%;
    width: 100%;
}

.swiper-slide {
    &__image-wrapper {
        width: 100%;
        height: 100%;
    } 

    &__image-inner {
        background-size: cover;
        background-position: center center;
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
        width: 100%;
        height: 100%;
        background-color: hsla(0, 0%, 0%, 0.2);
