        bottom: 0;
        cursor: pointer;  
        z-index: 10;

        @media screen and (max-width: 700px) {
        width: 33px;
        height: 114px;
    }
}

    &__drag {
        width: 36px;
        height: 36px !important;
        border-radius: 50%;
        display: flex;
        justify-content: center;
        align-items: center;
        transition: .2s transform,.2s top;
        border: 2px solid $black;
        background-color: $white;
        color: $black;
        font-size: 16px;
        cursor: grab;
        position: absolute;
        z-index: 15;

        @media screen and (max-width: 700px) {
            width: 28px;
            height: 28px !important;
        }

        &::before,
        &::after {
            content: '';
            position: absolute;
            width: 2px;
            height: 14px;
            background-color: $black;

            @media screen and (max-width: 700px) {
                height: 10px;
            }
        }

        &::before {
            top: -16px;
            left: calc(50% - 1px);

            @media screen and (max-width: 700px) {
                top: -10px;
            }
        }

        &::after {
            bottom: -16px;
            left: calc(50% - 1px);

            @media screen and (max-width: 700px) {
                bottom: -10px;
            }
        }
    }

    &__line {
        position: absolute;
        background-color: $white;
        width: 2px;
        height: 150px;
        right: 65%;
        top: auto;
        bottom: 30px;
        cursor: pointer;

        @media screen and (max-width: 700px) {
            height: 84px;
            right: 55%;
        }
    }
}

