.cards {
   &__wrapper {
     padding: 30px 0;

     @media screen and (max-width: 500px) {
       padding: 15px 0;
     }
   }

   &__header-wrapper {
     border-bottom: 2px solid $grey;
   }

   &__header {
     padding: 20px 0;
     max-width: 750px;
     display: flex;
     justify-content: space-between;
     font-size: 20px;
     align-items: center;

     @media screen and (max-width: 750px) {
       flex-direction: column;
     }
   }

   &__title {
     font-weight: 600;
     font-size: 26px;

     @media screen and (max-width: 750px) {
       margin-bottom: 20px;
     }
   }

   &__button-wrapper {
     max-width: 800px;
     padding: 40px 10px;
     display: flex;
     justify-content: space-around;
     margin: 0 auto;

     @media screen and (max-width: 750px) {
       justify-content: center;
       flex-wrap: wrap;
       max-width: 530px;
       gap: 15px;
     }

     @media screen and (max-width: 500px) {
       column-gap: 30px;
       row-gap: 20px;
     }
   }

   &__body {
     display: grid;
     grid-template-columns: repeat(4, 1fr);
     column-gap: 10px;
     row-gap: 50px;
     justify-items: center;
     padding-top: 20px;

     @media screen and (max-width: 1180px) {
       grid-template-columns: repeat(3, 1fr);
     }

     @media screen and (max-width: 900px) {
       grid-template-columns: repeat(2, 1fr);
     }

     @media screen and (max-width: 530px) {
       grid-template-columns: repeat(1, 1fr);
     }
   }

   &__footer-buttons {
     display: flex;
     justify-content: center;
     margin: 50px 0 35px;
     padding-bottom: 30px;
   }
 }

 .card {
   transition: all 0.3s ease-in-out;
   padding: 15px;

   &:hover {
     box-shadow: 5px 5px 15px rgba(0, 0, 0, 0.6);

     @media screen and (max-width: 900px) {
       box-shadow: none;
     }
   }

   &__image-wrapper {
     position: relative;
   }

   &:hover .card__options {
     opacity: 1;
     z-index: 10;
   }

   &__image {
     width: 245px;
     height: 290px;
     object-fit: cover;
   }

   &__options {
     position: absolute;
     bottom: 20px;
     left: 50%;
     transform: translateX(-50%);
     opacity: 0;
     z-index: -1;
     transition: all 0.3s ease-in-out;
   }

   &__options-buttons {
     display: flex;
     gap: 3px;
   }

   &__label {
     padding: 5px;
     transform: rotate(-90deg);
     position: absolute;
     text-align: center;
     font-weight: 600;
     font-size: 14px;
     line-height: 16px;

     &-off {
       border: 2px solid $grey;
       color: $grey;
       background-color: $white;
       top: 20px;
       left: -20px;
       height: 30px;
       width: 70px;
     }

     &-new {
       border: 2px solid $black;
       color: $white;
       background-color: $black;
       top: 10px;
       left: -10px;
       height: 30px;
       width: 50px;
     }

     &-top {
       border: 2px solid $black;
       color: $black;
       background-color: $white;
       top: 27px;
       left: -27px;
       height: 30px;
       width: 85px;
     }
   }

   &__info {
     display: flex;
     flex-direction: column;
   }

   &__name {
     padding-top: 27px;
     font-weight: 600;
     font-size: 15px;
   }

   &__price {
     padding: 7px 0 10px 0;
     font-weight: 600;
     font-size: 15px;
   }

   &__color {
     display: flex;
     gap: 8px;
     padding: 5px 0;

     button {
       width: 17px;
       height: 12px;
       border: none;
       transition: all 0.2s ease-in;
       cursor: pointer;

       &:hover {
         box-shadow: 3px 3px 15px rgba(0, 0, 0, 0.6);
         transform: scale(1.2);
       }
     }

     &_active {
       border: solid 1px $white;
       transform: scale(1.3);
     }

     &-first {
       background-color: $black;
     }

     &-second {
       background-color: $grey;
     }

     &-third {
       background-color: $accent;
     }
   }

   &__large-image {
     position: fixed;
     top: 0;
     left: 0;
     width: 100%;
     height: 100%;
     background-color: rgba(0, 0, 0, 0.8);
     display: flex;
     justify-content: center;
     align-items: center;
     z-index: 9999;
     cursor: pointer;

     img {
       max-width: 90%;
       max-height: 90%;
     }
   }
}

