sources.app/modules/cart/cart.scss:
.shopping__cart {
   display: none;
   font-family: "Ysabeau", serif;
   font-weight: 500;
   margin: 20px 0;
   background: $white;
   width: 350px;
   position: absolute;
   top: 40px;
   right: -10px;
   z-index: 999;
   border-radius: 3px;
   padding: 50px 20px 20px;
   border: 2px solid $black;
   box-shadow: 5px 5px 20px rgba(255, 255, 255, 0.8);

      @media screen and (max-width: 1165px) {
         top: 45px;
      }

      @media screen and (max-width: 450px) {
         width: 280px;
      }

   &-header {
      border-bottom: 2px solid $accent;
      padding-bottom: 10px;
      display: flex;
      align-items: flex-end;

      h3 {
         font-size: 22px;
      }

      &-count {
         padding: 10px 10px 0 5px;
      }

      img {
         height: 20px;
         margin-right: 7px;
         margin-left: 7px;
      }

      &-count {
         position: relative;
      }
   }

   &-close {
      position: absolute;
      top: 12px;
      right: 20px;
      padding: 5px;
      transition: all .3s ease-in;
      cursor: pointer;

      &:hover {
         transform: scale(1.4) rotate(180deg);
      }

      img {
         height: 28px;
      }
   }

   &-item {
      margin-right: 18px;
      }

      &-delete-item {
      border: none;
      background: transparent;
      cursor: pointer;
      transition: all .3s ease-in;

      &:hover {
         transform: scale(1.1);    
      }
   }

   &-empty {
      display: flex;
      justify-content: center;
      align-items: center;
      flex-direction: column;
      padding: 25px;

      img {
         width: 50px;
         height: 50px;
      }

      span {
         font-family: "Ysabeau", serif;
         font-size: 20px;
         padding-top: 10px;
      }
   }
}

.shopping__cart-open {
   display: block;
}

.shopping__cart-list {
   max-height: 350px;
   overflow: scroll;

   padding: 10px 0;
   border-bottom: 2px solid $accent;

   h4 {
   font-size: 14px;
   }

   li {
   display: flex;
   align-items: center;

   &:not(:last-child) {
      margin-bottom: 18px;
   }
   }


   .item-img {
      margin-right: 12px;
      width: 40px;
      height: 50px;
   }

   .item-name {
      font-size: 20px;
   }

   .item-price {
      font-size: 17px;
      color: $grey;
      margin-right: 8px;
   }

   &_wrapper {
      width: 100%;
      display: flex;
      flex-direction: column;
      justify-content: center;
   }

   &_items-wrapper {
      width: 100%;
      display: flex;
      justify-content: space-between;
   }
}

.shopping__cart:after {
   bottom: 100%;
   left: 89%;
   border: solid transparent;
   content: " ";
   height: 0;
   width: 0;
   position: absolute;
   border-bottom-color: $black;
   border-width: 8px;
}

.shopping__cart-item:after {
   content: "";
   display: table;
   clear: both;
}

.shopping__cart-footer{
   display: flex;
   justify-content: space-between;
   align-items: center;
   font-size: 20px;
   padding-top: 10px;

   &-btn {
   margin-top: 20px;
   }

   &-total {
   margin-top: 10px;
   }
}

.cart__overlay {
   position: fixed;
   top: 0;
   left: 0;
   width: 100%;
   height: 100%;
   background-color: rgba(0, 0, 0, 0.65);
   display: none;
   z-index: 100;
}

